package org.connecttag.utilities.global.deviceinfoimport android.content.Contextimport android.content.Intentimport android.content.IntentFilterimport android.os.BatteryManagerclass BatteryInformation {        var batteryLevel: String? = null        var isCharging: Boolean? = null        fun readDeviceInfo(context: Context) {            try {                val intent = context.registerReceiver(null, IntentFilter(Intent.ACTION_BATTERY_CHANGED))                val level = intent?.getIntExtra(BatteryManager.EXTRA_LEVEL, -1)                val scale = intent?.getIntExtra(BatteryManager.EXTRA_SCALE, -1)                batteryLevel = ((level?.toFloat() ?: (0f / scale?.toFloat()!!))).toInt().toString()            } catch (_: Exception) {            }        }    }