package org.connecttag.utilities.global.deviceinfoimport java.util.Localeclass DeviceLanguageInformation {        var language: String? = null        private var displayLanguage: String? = null    var displayLanguageName: String? = null        var iso3Language: String? = null        var country: String? = null    var displayCountry: String? = null    var iso3Country: String? = null    init {        readDeviceInfo()    }        fun readDeviceInfo() {            try {                language = Locale.getDefault().language.toString()            } catch (_: Exception) {            }            try {                displayLanguage = Locale.getDefault().displayLanguage.toString()            } catch (_: Exception) {            }            try {                iso3Language = Locale.getDefault().isO3Language.toString()            } catch (_: Exception) {            }            try {                displayLanguageName = Locale.getDefault().displayName.toString()            } catch (_: Exception) {            }            try {                displayCountry = Locale.getDefault().displayCountry.toString()            } catch (_: Exception) {            }            try {                country = Locale.getDefault().country.toString()            } catch (_: Exception) {            }            try {                iso3Country = Locale.getDefault().isO3Country.toString()            } catch (_: Exception) {            }        }    }