package org.connecttag.utilities.global.deviceinfoimport android.content.Contextclass ScreenInformation {        var width: String? = null        var height: String? = null        var density: String? = null        var densityDpi: String? = null        var yDpi: String? = null        var xDpi: String? = null        fun readDeviceInfo(context: Context) {            val displayMetrics = context.resources.displayMetrics            try {                width = displayMetrics.widthPixels.toString()            } catch (_: Exception) {            }            try {                height = displayMetrics.heightPixels.toString()            } catch (_: Exception) {            }            try {                density = displayMetrics.density.toString()            } catch (_: Exception) {            }            try {                densityDpi = displayMetrics.densityDpi.toString()            } catch (_: Exception) {            }            try {                yDpi = displayMetrics.ydpi.toString()            } catch (_: Exception) {            }            try {                xDpi = displayMetrics.xdpi.toString()            } catch (_: Exception) {            }        }    }