package org.connecttag.utilities.global.enumsimport android.content.Contextimport android.graphics.Colorenum class AlertType {    Dangers(Color.RED, "خطر"),    Warning(Color.YELLOW,"تنبية"),    Caution(Color.MAGENTA,"احذر"),    Notice(Color.WHITE,"اشعار"),    Safety(Color.GREEN,"نجح"),    Normal(Color.YELLOW,"تنبية");    var color: Int? = null    var title : String? = null    constructor()    // custom constructors    constructor(        color: Int,        title: String    ) {        this.color = color        this.title = title    }    fun toArgb(context: Context) =        color?.let { context.resources.getColor(it, null) } // Deprecated in API level 23}